    public static List<Integer> findSubstring(String s, String[] words) {
        int wordLength = words[0].length();
        int totalLength = wordLength * words.length;
        List<Integer> ans = new ArrayList<>();
        if(s.length() < totalLength) {
            return ans;
        }
        Map<String, Integer> wordCount = new HashMap<>();
        for(String str: words) {
            wordCount.put(str, wordCount.getOrDefault(str, 0) + 1);
        }
        for(int i=0; i < wordLength; i++) {
            Map<String, Integer> currentWordCount = new HashMap<>();
            int curr = 0;
            int startIndex = i;
            int wordStartIndex = i;
            while(wordStartIndex <= s.length() - wordLength) {
                String str = s.substring(wordStartIndex, wordStartIndex + wordLength);
                if(!wordCount.containsKey(str)) {
                    currentWordCount = new HashMap<>();
                    startIndex = startIndex + wordLength;
                    wordStartIndex = startIndex;
                } else if (wordCount.get(str).equals(currentWordCount.getOrDefault(str, 0))) {
                    String strToRemove = s.substring(startIndex, startIndex + wordLength);
                    if(currentWordCount.get(strToRemove) == 1) {
                        currentWordCount.remove(strToRemove);
                    } else {
                        currentWordCount.put(strToRemove, currentWordCount.get(strToRemove) - 1);
                    }
                    startIndex = startIndex + wordLength;
                } else {
                    currentWordCount.put(str, currentWordCount.getOrDefault(str, 0) + 1);
                    wordStartIndex = wordStartIndex + wordLength;
                    if((wordStartIndex - startIndex) == totalLength) {
                        ans.add(startIndex);
                    }
                }
            }
        }
        return ans;
    }


