    public static List<Integer> findSubstring(String s, String[] words) {
        long start = System.nanoTime();
        if(s.isBlank()) return new ArrayList<>();
        int wordlength = words[0].length();
        int arraylength = words.length;
        int totalwordslength=arraylength*wordlength;
        if(arraylength<1||arraylength>5000) return new ArrayList<>();

        HashMap<String,Integer> map = new HashMap<>();

        for(String x : words)
            map.put(x, map.getOrDefault(x,0)+1);

        List<Integer> result = new ArrayList<>();

        int slength = s.length();

        for(int i =0; (i+totalwordslength)<=slength;i++){
            HashMap<String,Integer> tmp= new HashMap<>(map);

//            tmp = map;       // store the word map with frequency     // this is not correct as it creates both objects same reference
            StringBuilder str = new StringBuilder(s.substring(i,totalwordslength+i));
            boolean available = true;

//            int indexofword;
//            for(String word: words){
//                indexofword = str.indexOf(word);
//                while (indexofword%wordlength!=0 && indexofword+1<str.length() && indexofword!=-1) {
//                    indexofword = str.indexOf(word,indexofword+1);
//                }
//                if(indexofword != -1 ){
//                    str.replace(indexofword,indexofword+wordlength,"");
//                } else {
//                    available = false;
//                    break;
//                }
//            }

            for (int j=0; j+wordlength<=str.length(); j+=wordlength){
                String word = str.substring(j,j+wordlength);
                if(!map.containsKey(word)){
                    available=false;
                    break;
                }
                int newval = tmp.getOrDefault(word,0)-1; // if not available returns default -1, if it below frequency returns -1 and break
                if (newval != -1) {
                    tmp.put(word, newval);
                }
                else {
                    available = false;
                    break;
                }
            }
            if (available) {
                result.add(i);
            }
        }
        long elapsedTime = (System.nanoTime() - start) / 1000000;
        System.out.println(elapsedTime+" ms");

        return result;
    }
